INSERT INTO `ejercicios` VALUES (1,'Jugadores cuya tercera letra de su nombre sea la \'v\'.','SELECT Nombre FROM jugadores WHERE Nombre LIKE \'__v%\';','Simple','NBA'),(2,'Mostrar un listado de jugadores con el formato Nombre(Equipo) en una sola columna.','SELECT CONCAT(Nombre,\'(\',Nombre_equipo,\')\') FROM jugadores;','Simple','NBA'),(3,'Primeros 10 jugadores por orden alfabético.','SELECT Nombre FROM jugadores ORDER BY Nombre ASC LIMIT 10;','Simple','NBA'),(4,'Nombre de las divisiones de la Conferencia Este.','SELECT DISTINCT Division FROM equipos WHERE Conferencia=\'east\';','Simple','NBA'),(5,'Obtener cuántas letras tiene el nombre de cada jugador de los \'Grizzlies\'. (Usar función\r\nLENGTH)','SELECT Nombre,LENGTH(Nombre) AS letras FROM jugadores WHERE nombre_equipo=\'Grizzlies\';','Simple','NBA'),(7,'Número de jugadores argentinos de la NBA.\0','SELECT COUNT(codigo) AS nº_argentinos FROM jugadores WHERE procedencia=\'Argentina\';\0','Simple','NBA'),(8,'Número de jugadores de Italia y Francia de la NBA.\0','SELECT COUNT(codigo) AS italianos_y_franceses FROM jugadores WHERE procedencia=\'Italia\' OR procedencia=\'Francia\';\0','Simple','NBA'),(9,'Número de pivots \'C\' que tiene cada equipo.\0','SELECT Nombre_equipo,COUNT(codigo) AS pivots FROM jugadores WHERE posicion=\'C\' GROUP BY nombre_equipo;\0','Simple','NBA'),(10,'¿Cuánto mide el pívot más alto de la NBA?\0','SELECT MAX(altura) FROM jugadores WHERE posicion=\'C;\0','Simple','NBA'),(11,'Número de jugadores que empiezan por \'R\'.\0','SELECT COUNT(nombre) FROM jugadores WHERE nombre LIKE \'R%\';\0','Simple','NBA'),(12,'Peso medio de los jugadores de los \'Raptors\'.\r\n\0','SELECT  AVG(Peso) AS peso_medio FROM jugadores WHERE Nombre_equipo=\'Raptors\';\0','Simple','NBA'),(13,'Número de equipos que tiene cada conferencia.\0','SELECT Conferencia,COUNT(Nombre) AS nº_equipos FROM equipos GROUP BY conferencia;\0','Simple','NBA'),(14,'Obtener cuántas letras tiene el nombre de cada jugador de los Grizzlies. (Usar función LENGTH).\0','SELECT Nombre,LENGTH(Nombre) AS letras FROM jugadores WHERE Nombre_equipo=\'Grizzlies\';\0','Simple','NBA'),(15,'Obtener la ciudad y el teléfono de las oficinas de Estados Unidos.\r\n\0','SELECT Ciudad,Telefono FROM Oficinas WHERE Pais=\'EEUU\';\0','Simple','Jardinería'),(16,'Obtener el cargo, nombre, apellidos e email del jefe de la empresa.\0','SELECT Puesto,Nombre,CONCAT(Apellido1,\' \',Apellido2) AS apellidos,Email FROM Empleados WHERE CodigoJefe IS NULL;\0','Simple','Jardinería'),(17,'Sacar el nombre, apellidos y cargo de aquellos trabajadores que no sean representantes de ventas.\0','SELECT Nombre,CONCAT(Apellido1,\' \',Apellido2) AS apellidos,Puesto FROM Empleados WHERE Puesto<>\'Representante Ventas\';\0','Simple','Jardinería'),(18,'Obtener el número de clientes que tiene la empresa.\0','SELECT COUNT(CodigoCliente) FROM Clientes;\0','Simple','Jardinería'),(19,'Obtener el nombre de los clientes españoles.\0','SELECT NombreCliente FROM Clientes WHERE Pais=\'Spain\';\0','Simple','Jardinería'),(20,'Obtener cuántos clientes tiene cada país.\0','SELECT Pais,COUNT(CodigoCliente) FROM Clientes GROUP BY Pais;\0','Simple','Jardinería'),(21,'Obtener el número de clientes que tiene la ciudad de Madrid.\0','SELECT COUNT(CodigoCliente) FROM Clientes WHERE Ciudad=\'Madrid\';\0','Simple','Jardinería'),(22,'Obtener el código de empleado y el número de clientes al que atiende cada representante de ventas.\0','SELECT CodigoEmpleadoRepVentas,COUNT(CodigoCliente) FROM Clientes GROUP BY CodigoEmpleadoRepVentas;\0','Simple','Jardinería'),(23,'Obtener el primer y último pago que hizo el cliente con código de cliente 3.\0','SELECT MIN(FechaPago),MAX(FechaPago) FROM Pagos WHERE CodigoCliente=3;\0','Simple','Jardinería'),(24,'Obtener el código de cliente de aquellos clientes que hicieron pagos en 2008.\r\n\0','SELECT DISTINCT CodigoCliente FROM Pagos WHERE YEAR(FechaPago)=2008;\0','Simple','Jardinería'),(25,'Obtener los distintos estados por los que puede pasar un pedido.\0','SELECT DISTINCT Estado FROM Pedidos;\0','Simple','Jardinería'),(26,'Obtener el número de pedido, código de cliente, fecha requerida y fecha de entrega de los pedidos que no han sido entregado a tiempo.\0','SELECT  CodigoPedido,CodigoCliente,FechaEsperada,FechaEntrega FROM Pedidos WHERE FechaEntrega>FechaEsperada;\0','Simple','Jardinería'),(27,'Obtener cuántos productos existen en cada línea de pedido.\0','SELECT NumeroLinea,SUM(Cantidad) AS productos FROM DetallePedidos GROUP BY Numero\r\nLinea;\0','Simple','Jardinería'),(28,'Obtener un listado de los 20 códigos de productos más pedidos ordenado por cantidad pedida.\0','SELECT  CodigoProducto,SUM(Cantidad) AS total_cantidad FROM DetallePedidos GROUP BY CodigoProducto ORDER BY SUM(Cantidad) DESC LIMIT 20;\0','Simple','Jardinería'),(29,'Obtener el número de pedido, código de cliente, fecha requerida y fecha de entrega de los pedidos cuya fecha de entrega ha sido al menos dos días antes de la fecha requerida. (Usar la función ADDDate).\r\n\0','SELECT CodigoPedido,CodigoCliente,FechaEsperada,FechaEntrega FROM Pedidos WHERE ADDDATE(FechaEntrega,INTERVAL 2 DAY) <= FechaEsperada;\0','Simple','Jardinería'),(30,'Obtener un listado con el precio total de cada pedido.\0','SELECT CodigoPedido,SUM(Cantidad*PrecioUnidad) AS precio_pedido FROM DetallePedidos GROUP BY  CodigoPedido;\0','Simple','Jardinería'),(31,'Obtener cuántos pedidos tiene cada cliente en cada estado.\0','SELECT CodigoCliente,Estado,COUNT(CodigoPedido) AS nº_pedidos FROM Pedidos GROUP BY CodigoCliente,Estado;\0','Simple','Jardinería'),(32,'Obtener la media de peso de los jugadores de cada equipo.\0','SELECT Nombre_equipo,AVG(Peso) FROM jugadores GROUP BY Nombre_equipo;\0','Simple','NBA'),(33,'Obtener los equipos cuya media de peso de los jugadores sea superior a 228 libras.\0','SELECT Nombre_equipo,AVG(Peso) AS media FROM jugadores GROUP BY Nombre_equipo HAVING media>228;\0','Simple','NBA'),(34,'Obtener el número de jugadores españoles de cada equipo.\0','SELECT Nombre_equipo,COUNT(codigo) FROM jugadores WHERE Procedencia=\'Spain\' GROUP BY Nombre_equipo;\0','Simple','NBA'),(35,'Obtener el nombre de los equipos con más de un jugador español.\0','SELECT Nombre_equipo,COUNT(codigo) FROM jugadores WHERE Procedencia=\'Spain\' GROUP BY Nombre_equipo HAVING COUNT(codigo)>1;\0','Simple','NBA'),(36,'Obtener una lista con el código de oficina, ciudad, región y país de aquellas oficinas que están en países que empiecen por \'E\'.\0','SELECT CodigoOficina,Ciudad,Region,Pais FROM Oficinas WHERE Pais LIKE \'E%\';\0','Simple','Jardinería'),(37,'Obtener nombre,gama,dimensiones,cantidad en stock y el precio de venta de los cinco productos más caros.\0','SELECT Nombre,Gama,Dimensiones,CantidadEnStock,PrecioVenta FROM Productos ORDER BY PrecioVenta DESC LIMIT 5;\r\n\0','Simple','Jardinería'),(38,'Obtener la media de peso de aquellos jugadores cuya procedencia sea de España, Francia e Italia.\0','SELECT Procedencia,AVG(peso) AS media_peso FROM jugadores GROUP BY Procedencia HAVING Procedencia IN(\'Spain\',\'France\',\'Italy\');\0','Simple','NBA'),(39,'Obtener el código y la facturación de aquellos pedidos mayores de 2000 euros.\0','SELECT CodigoPedido,SUM(Cantidad*PrecioUnidad) AS facturacion FROM DetallePedidos GROUP BY CodigoPedido HAVING facturacion>2000;\0','Simple','Jardinería'),(40,'Obtener una lista de los productos mostrando el stock total, la gama y el proveedor.\r\n\0','SELECT Nombre,SUM(CantidadEnStock) AS stock_total,Gama,Proveedor FROM Productos GROUP BY Nombre,Gama,Proveedor;\r\n\0','Simple','Jardinería'),(41,'Sacar número de pedidos y código de cliente de aquellos pedidos cuya fecha de pedido sea menor o igual que la fecha de entrega.\r\n\0','SELECT CodigoCliente,COUNT(codigopedido) AS nº_pedidos FROM Pedidos WHERE FechaPedido<=FechaEntrega GROUP BY CodigoCliente;\0','Simple','Jardinería'),(42,'Obtener el nombre del producto más caro.\0','SELECT Nombre FROM Productos WHERE PrecioVenta=(SELECT MAX(PrecioVenta) FROM Productos);\0','Subconsulta','Jardinería'),(43,'Obtener el nombre del producto del que más unidades se hayan vendido en un mismo pedido.\0','SELECT Nombre FROM Productos WHERE CodigoProducto=(SELECT CodigoProducto FROM DetallePedidos ORDER BY Cantidad DESC LIMIT 1);\0','Subconsulta','Jardinería'),(44,'Obtener los jugadores de la NBA que pesen más que la media de todos los jugadores españoles.\0','SELECT Nombre FROM jugadores WHERE Peso>(SELECT AVG(Peso) FROM jugadores WHERE Procedencia=\'Spain\');\0','Subconsulta','NBA'),(45,'Obtener los puntos por partido de \'Pau Gasol\' en toda su carrera.\0','SELECT temporada,Puntos_por_partido FROM estadisticas WHERE jugador=(SELECT codigo FROM jugadores WHERE Nombre=\'Pau Gasol\');\0','Subconsulta','NBA'),(46,'Obtener los puntos por partido de los jugadores  de los \'Cavaliers\' en la temporada 2007/2008.\0','SELECT jugador,Puntos_por_partido FROM estadisticas WHERE jugador IN (SELECT codigo FROM jugadores WHERE Nombre_equipo=\'Cavaliers\') AND temporada=\'07/08\';\0','Subconsulta','NBA'),(47,'Obtener el número de jugadores que tiene cada equipo de la conferencia oeste (West).\0','SELECT  Nombre_equipo,COUNT(codigo) AS nº_jugadores FROM jugadores WHERE Nombre_equipo IN (SELECT Nombre FROM equipos WHERE Conferencia=\'West\') GROUP BY Nombre_equipo;\0','Subconsulta','NBA'),(48,'Obtener la máxima media de puntos de \'Lebron James\' en su carrera.\0','SELECT MAX(Puntos_por_partido) FROM estadisticas WHERE jugador=(SELECT codigo FROM jugadores WHERE Nombre=\'Lebron James\');\0','Subconsulta','NBA'),(49,'Obtener las asistencias por partido de \'José Calderon\' en la temporada \'07/08\'.\0','SELECT Asistencias_por_partido FROM estadisticas WHERE jugador=(SELECT codigo FROM jugadores WHERE Nombre=\'Jose Calderon\') AND temporada=\'07/08\';\0','Subconsulta','NBA'),(50,'Obtener los puntos por partido de \'Lebron James\' en las temporadas del \'03/04\' al \'05/06\'.\0','SELECT temporada,Puntos_por_partido FROM estadisticas WHERE jugador IN(SELECT codigo FROM jugadores WHERE Nombre=\'Lebron James\') AND temporada IN (\'03/04\',\'04/05\',\'05/06\');\0','Subconsulta','NBA'),(51,'Obtener la media de rebotes de los jugadores de la conferencia Este (East).\0','SELECT jugador,AVG(Rebotes_por_partido) FROM estadisticas WHERE jugador IN(SELECT codigo FROM jugadores WHERE Nombre_equipo IN(SELECT Nombre FROM equipos WHERE Conferencia=\'East\')) GROUP BY jugador;\0','Subconsulta','NBA'),(52,'Obtener los rebotes por partido de los jugadores de los equipos de la ciudad de Los Angeles durante la temporada 2007/2008.\r\n\0','SELECT jugador,Rebotes_por_partido FROM estadisticas WHERE jugador IN(SELECT codigo FROM jugadores WHERE Nombre_equipo IN(SELECT Nombre FROM equipos WHERE Ciudad=\'Los Angeles\')) AND temporada=\'07/08\';\0','Subconsulta','NBA'),(53,'Obtener el número de jugadores que tiene cada equipo de la división NorthWest.\0','SELECT Nombre_equipo,COUNT(codigo) FROM jugadores WHERE nombre_equipo IN (SELECT Nombre FROM equipos WHERE Division=\'NorthWest\') GROUP BY Nombre_equipo;\0','Subconsulta','NBA'),(54,'Obtener la temporada con más puntos por partido de Kobe Bryant.\0','SELECT temporada,Puntos_por_partido FROM estadisticas WHERE jugador=(SELECT codigo FROM jugadores WHERE Nombre=\'Kobe Bryant\') ORDER BY Puntos_por_partido DESC LIMIT 1;\0','Subconsulta','NBA'),(55,'Obtener el número de bases (G) que tiene cada equipo de la conferencia este \'East\'.\0','SELECT Nombre_equipo,COUNT(codigo) FROM jugadores WHERE Posicion=\'G\' AND Nombre_equipo IN (SELECT Nombre FROM equipos WHERE Conferencia=\'East\') GROUP BY Nombre_equipo;\0','Subconsulta','NBA'),(56,'Obtener el máximo reboteador de los Suns en la temporada 2007/2008.\0','SELECT Nombre FROM jugadores WHERE codigo=(SELECT  jugador FROM estadisticas WHERE temporada=\'07/08\'  and jugador IN(SELECT codigo FROM jugadores WHERE Nombre_equipo=\'Suns\') ORDER BY Rebotes_por_partido DESC LIMIT 1);\0','Subconsulta','NBA'),(57,'Obtener cuántas letras tiene el equipo con nombre más largo de la NBA (ciudad y nombre).\0','SELECT Nombre,Ciudad FROM equipos WHERE LENGTH(Nombre)=(SELECT MAX(LENGTH(Nombre)) FROM equipos);\0','Subconsulta','NBA'),(58,'Obtener el nombre de los clientes que hayan hecho pedidos en 2008.\0','SELECT NombreCliente FROM Clientes WHERE CodigoCliente IN (SELECT CodigoCliente FROM Pedidos WHERE YEAR(FechaPedido)=2008);\0','Subconsulta','Jardinería'),(59,'Obtener los clientes que han pedido más de 200 unidades de cualquier producto.\0','SELECT NombreCliente FROM Clientes WHERE CodigoCliente IN (SELECT CodigoCliente FROM Pedidos WHERE CodigoPedido IN (SELECT CodigoPedido FROM DetallePedidos WHERE Cantidad>200));\0','Subconsulta','Jardinería'),(60,'Obtener los clientes que residan en la misma ciudad donde hay una oficina, indicando dónde está la oficina.\0','SELECT NombreCliente,Ciudad FROM Clientes WHERE Ciudad IN(SELECT Ciudad FROM Oficinas);\r\n\0','Subconsulta','Jardinería'),(61,'Obtener el nombre, los apellidos y el email de los empleados a cargo de Alberto Soria.\0','SELECT Nombre,CONCAT(Apellido1,\' \',Apellido2) AS apellidos,Email FROM Empleados WHERE CodigoJefe=(SELECT CodigoEmpleado FROM Empleados WHERE Nombre=\'Alberto\' AND Apellido1=\'Soria\');\0','Subconsulta','Jardinería'),(62,'Obtener el nombre de los clientes a los que no se les ha entregado a tiempo un pedido (fechaentrega>fechaesperada).\r\n\0','SELECT NombreCliente FROM Clientes WHERE CodigoCliente IN (SELECT CodigoCliente FROM Pedidos WHERE FechaEntrega>FechaEsperada);\0','Subconsulta','Jardinería'),(63,'Obtener los puntos por partido de los jugadores que jueguen en los Timberwolves en la temporada 2005/2006.\0','SELECT jugador,Puntos_por_partido FROM estadisticas WHERE jugador IN(SELECT codigo FROM jugadores WHERE Nombre_equipo=\'TimberWolves\') AND temporada=\'05/06\';\0','Subconsulta','NBA'),(64,'Obtener el nombre y el peso de los jugadores de la NBA que hayan hecho más de 25 puntos por partido y ordenarlos alfabéticamente.\0','SELECT Nombre,Peso FROM jugadores WHERE codigo IN(SELECT jugador FROM estadisticas WHERE Puntos_por_partido>25) ORDER BY Nombre ASC;\0','Subconsulta','NBA'),(65,'Obtener las asistencias por partido y los tapones por partido de los Heat en la temporada 2005/2006.\0','SELECT jugador,Asistencias_por_partido,Tapones_por_partido FROM estadisticas WHERE jugador IN(SELECT codigo FROM jugadores WHERE Nombre_equipo=\'Heat\') AND temporada=\'05/06\';\0','Subconsulta','NBA'),(66,'Obtener la media de puntos por partido y la media de asistencias por partido de los Timberwolves en la temporada 2007/2008.\0','SELECT jugador,puntos_por_partido,asistencias_por_partido FROM estadisticas WHERE jugador IN (SELECT codigo FROM jugadores WHERE Nombre_equipo=\'TimberWolves\') AND temporada=\'07/08\';\0','Subconsulta','NBA'),(67,'Obtener el nombre y teléfono de los clientes que hayan hecho algún pago en 2007 ordenados alfabéticamente por nombre.\0','SELECT NombreCliente,Telefono FROM Clientes WHERE CodigoCliente IN(SELECT CodigoCliente FROM Pagos WHERE YEAR(FechaPago)=2007) ORDER BY NombreCliente ASC;\0','Subconsulta','Jardinería'),(68,'Obtener la media de puntos por temporada de los jugadores que se llamen \'Steve\' y pesen más de 200 libras.\0','SELECT temporada,jugador,Puntos_por_partido FROM estadisticas WHERE jugador IN (SELECT  codigo FROM jugadores WHERE Nombre LIKE \'Steve%\' AND peso>200);\0','Subconsulta','NBA'),(69,'Obtener la gama, el proveedor y la cantidad de aquellos productos cuyo estado sea pendiente.\0','SELECT DISTINC Nombre,Gama,Proveedor,CantidadEnStock FROM Productos WHERE CodigoProducto IN (SELECT CodigoProducto FROM DetallePedidos WHERE CodigoPedido IN (SELECT CodigoPedido FROM Pedidos WHERE Estado=\'pendiente\'));\0','Subconsulta','Jardinería'),(70,'Obtener el nombre, peso y altura de los jugadores que juegan en la ciudad de Los Ángeles y que sean españoles.\0','SELECT Nombre,Altura,Peso FROM jugadores WHERE Nombre_equipo IN (SELECT Nombre FROM equipos WHERE Ciudad=\'Los Angeles\') AND Procedencia=\'Spain\';\0','Subconsulta','NBA'),(71,'Obtener los puntos por partido de los jugadores de Los Lakers en la temporada 2007/2008.\0','SELECT jugador,Puntos_por_partido FROM estadisticas WHERE jugador IN(SELECT codigo FROM jugadores WHERE Nombre_equipo=\'Lakers\') AND temporada=\'07/08\';\0','Subconsulta','NBA'),(72,'Obtener la ciudad con el equipo cuya media de altura de los jugadores sea la más baja.\0','SELECT Ciudad,Nombre FROM equipos WHERE Nombre=(SELECT Nombre_equipo FROM jugadores GROUP BY Nombre_equipo ORDER BY AVG(Altura) LIMIT 1);\0','Subconsulta','NBA'),(73,'Obtener el nombre de los clientes y el nombre de sus representantes junto con la ciudad de la oficina a la que pertenece el representante.\0','SELECT NombreCliente,Empleados.Nombre,Oficinas.Ciudad FROM Oficinas INNER JOIN Empleados ON Oficinas.CodigoOficina=Empleados.CodigoOficina INNER JOIN Clientes ON Empleados.CodigoEmpleado=Clientes.CodigoEmpleadoRepVentas;\0','Compuesta','Jardinería'),(74,'Obtener el nombre de los clientes que no hayan hecho pagos y el nombre de sus representantes junto con la ciudad de la oficina a la que pertenece el representante.\r\n\0','SELECT NombreCliente,Nombre,Oficinas.Ciudad FROM Clientes INNER JOIN Empleados ON CodigoEmpleadoRepVentas=CodigoEmpleado INNER JOIN Oficinas ON Oficinas.CodigoOficina=Empleados.CodigoOficina AND CodigoCliente NOT IN (SELECT CodigoCliente FROM Pagos);\0','Compuesta','Jardinería'),(75,'Obtener un listado de clientes indicando el nombre del cliente y cuántos pedidos ha realizado.\0','SELECT NombreCliente,COUNT(CodigoPedido) FROM Clientes INNER JOIN Pedidos ON Clientes.CodigoCliente=Pedidos.CodigoCliente GROUP BY NombreCliente;\0','Compuesta','Jardinería'),(76,'Obtener un listado con los nombres de los clientes y el total pagado por cada uno de ellos.\0','SELECT NombreCliente,SUM(Cantidad) AS Total_Pagado FROM Clientes INNER JOIN Pagos ON Clientes.CodigoCliente=Pagos.CodigoCliente GROUP BY NombreCliente;\0','Compuesta'